        public static void UpdateAllTextIcons()
        {
            TMPro.TextMeshProUGUI[] textFields = GameObject.FindObjectsOfType<TMPro.TextMeshProUGUI>();

            foreach (TMPro.TextMeshProUGUI textField in textFields)
                textField.UpdateIcons();
        }

        public static string Icon(string name, IconStyle style)
        {
            if (style == IconStyle.Regular)
            {
                if (RegularIcons.ContainsKey(name))
                    return "[R " + name + "]";
            }
            else
            {
                if (SolidIcons.ContainsKey(name))
                    return "[S " + name + "]";
            }

            return "";
        }

