        public static Dictionary<string, string> RegularIcons => Icons[(int)IconStyle.Regular];
        public static Dictionary<string, string> SolidIcons => Icons[(int)IconStyle.Solid];


        static string[] regularIconNames = null;
        public static string[] RegularIconNames
        {
            get
            {
                if (regularIconNames == null)
                {
                    regularIconNames = new string[RegularIcons.Keys.Count];
                    int i = 0;
                    foreach (KeyValuePair<string, string> icon in RegularIcons)
                    {
                        regularIconNames[i] = icon.Key;
                        i++;
                    }
                }
                return regularIconNames;
            }
        }

        static string[] solidIconNames = null;
        public static string[] SolidIconNames
        {
            get
            {
                if (solidIconNames == null)
                {
                    solidIconNames = new string[SolidIcons.Keys.Count];
                    int i = 0;
                    foreach (KeyValuePair<string, string> icon in SolidIcons)
                    {
                        solidIconNames[i] = icon.Key;
                        i++;
                    }
                }
                return solidIconNames;
            }
        }

        public enum IconStyle
        {
            Regular = 0,
            Solid = 1
        }
    }

    public static class TextMeshProUGUIExtension
    {
        public static void UpdateIcons(this TMPro.TextMeshProUGUI textField)
        {
            string text = textField.text;

            foreach (KeyValuePair<string, string> icon in UIIcons.RegularIcons)
            {
                text = text.Replace(string.Format("[R {0}]", icon.Key), string.Format("<font=\"FA5 Regular\">{0}</font>", icon.Value));
            }

            foreach (KeyValuePair<string, string> icon in UIIcons.SolidIcons)
            {
                text = text.Replace(string.Format("[S {0}]", icon.Key), string.Format("<font=\"FA5 Solid\">{0}</font>", icon.Value));
            }

            textField.text = text;
        }
    }
